vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 centered = uv - 0.5;
    float r = length(centered);
    float theta = atan2(centered.y, centered.x);

    // Create rotating pattern with triangular symmetry
    float timeRotation = iTime * 0.5;
    theta += timeRotation;

    // Use mod to create repeating sectors
    theta = mod(theta, PI * 2.0);
    float triangleSector = mod(theta * 3.0, PI * 2.0);

    // Create color variations using noise and turbulence
    vec2 noiseCoords = vec2(triangleSector, r) * 10.0;
    float c = 0.5 * r + 0.5 * turbulent(noiseCoords);

    // Convert to RGB with rotating colors
    vec3 color = hsl2rgb(vec3(triangleSector * 2.0, 1.0, c));

    return vec4(color, 1.0);
}
