vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create base noise patterns
    vec3 noise1 = vec3(noise(uv + time), noise(uv * 2.0 + time * 0.5), noise(uv * 0.5 + time));
    vec3 noise2 = vec3(turbulent(uv * 4.0 + time), turbulent(uv * 2.0 + time), turbulent(uv + time));

    // Create distortion effect
    vec2 distortedUV = uv + sin(time) * 0.1 * noise1.xy;
    distortedUV += sin(distortedUV * pi) * 0.2;

    // Color transformation with metallic feel
    vec3 color = hsl(
        mod(time * 0.5 + noise1.x, 1.0), 
        0.7 + noise1.z * 0.3,
        0.8 + noise2.x * 0.2
    );

    // Add shimmer effect
    float shimmer = sin(uv.x * pi + time) * sin(uv.y * pi + time * 0.5);
    color += vec3(0.1, 0.1, 0.2) * (1.0 + shimmer * 0.5);

    // Combine layers with noise
    color = mix(color, hsl2rgb(vec3(mod(time * 0.3 + noise2.y, 1.0), 0.6, 0.7)), noise2.z * 0.3);

    return vec4(color, 1.0);
}
