vec4 Effect2(vec2 uv) {
    // Create a base pattern using UV coordinates and time
    vec2 st = uv * 10.0 + iTime;

    // Add noise to create organic movement
    float n = noise(st);

    // Create a turbulence effect that changes over time
    float t = turbulent(vec3(uv, iTime));

    // Combine positional and temporal variations
    vec2 posVar = uv * 5.0 + vec2(sin(iTime), cos(iTime)) * 2.0;
    float noiseVar = noise(posVar);

    // Create color transitions using HSL to RGB conversion
    vec3 hslColor = vec3(
        sin(iTime) * 0.5 + 0.5,
        abs(cos(iTime)) * 0.7 + 0.3,
        abs(sin(uv.x * PI + iTime)) * 0.6 + 0.4
    );

    // Convert to RGB and mix with noise values
    vec3 rgbColor = hsl2rgb(hslColor);
    vec3 finalColor = rgbColor * (1.0 - n) + hsv2rgb(vec3(n, t, 1.0));

    return vec4(finalColor, 1.0);
}
