vec4 Effect2(vec2 uv) {
    // Create polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime; // Add time for rotation

    // Create color from angle and radius
    vec3 color = hsl2rgb(vec3(
        fract(a / (pi * 2.0)) * 360.0,
        sin(r * 5.0 + iTime) * 0.5 + 0.5,
        cos(r * 10.0 + a * 3.0) * 0.5 + 0.5
    ));

    // Add noise and turbulence
    float noiseVal = noise(vec2(a, r)) * 0.5;
    float turb = turbulent(vec2(a, r * 0.5)) * 0.5;

    color *= mix(1.0, 0.5, noiseVal + turb);

    return vec4(color, 1.0);
}
