vec4 Effect2(vec2 uv) {
    // Rotate coordinates over time
    float rot = sin(iTime * 0.5 + 1.0);
    vec2 rotatedUV = cos(rot) * (uv - 0.5) + sin(rot) * (uv.yx - 0.5) + 0.5;

    // Create polar coordinates
    vec2 toCenter = rotatedUV - 0.5;
    float radius = length(toCenter);
    float angle = atan2(toCenter.y, toCenter.x) + iTime;

    // Generate noise values with time variation
    float n1 = noise(vec3(rotatedUV * 4.0, iTime));
    float n2 = turbulent(vec2(angle * 2.0, radius * 5.0));
    float n3 = turbulent(vec3(rotatedUV * 8.0, iTime * 2.0));

    // Create color variations
    vec3 color1 = hsl(angle * 0.5 + 0.5, 0.6, 0.5);
    vec3 color2 = hsl(angle * 0.5 + 0.5 + iTime * 0.1, 0.7, 0.6);

    // Mix noise and colors
    vec3 finalColor = mix(color1, color2, n1 * 0.5 + 0.5);
    finalColor *= sin(radius * 8.0 + iTime) * 0.5 + 0.5;
    finalColor += n2 * 0.4;
    finalColor += n3 * 0.3;

    // Add brightness variation
    float brightness = sin(iTime * 0.2 + radius * 6.0) * 0.5 + 0.5;
    finalColor *= brightness;

    return vec4(finalColor, 1.0);
}
