vec4 Effect2(vec2 uv) {
    // Center coordinates
    vec2 st = uv - 0.5;

    // Polar coordinates
    float r = length(st);
    float theta = atan2(st.y, st.x);

    // Blade parameters
    float bladeWidth = 0.1;
    float max_r = 0.4 + turbulent(vec3(iTime * 0.1)) * 0.2;

    // Create three blades
    float blade1 = abs(sin(theta - PI/3.));
    float blade2 = abs(sin(theta + PI/3.));
    float blade3 = abs(sin(theta + PI));

    // Combine blades and create mask
    float blades = (blade1 + blade2 + blade3) / 3.0;
    blades = smoothstep(0.5, 1.0, blades);

    // Create circular boundary
    float circle = smoothstep(max_r - 0.1, max_r, r);

    // Combine elements
    vec3 color = hsl2rgb(vec3(60.0/360.0, 0.8, 0.5 + noise(vec3(iTime)) * 0.2));
    vec3 finalColor = mix(vec3(0.1), color, blades);

    // Add glow effect
    float glow = turbulent(vec2(r * 10.0, iTime * 0.1));
    finalColor += glow * 0.5;

    return vec4(finalColor * circle, 1.0);
}
