vec4 Effect2(vec2 uv) {
    // Create particle positions using noise and time
    vec3 pos = vec3(uv * 2.0 - 1.0, iTime);
    float speed = 0.5;

    // Generate particle movement based on mouse position
    vec2 mousePos = (uv + sin(iTime)) * 0.5 + 0.5;
    float distanceToMouse = length(uv - mousePos);

    // Create attraction/repulsion effect using noise
    float attract = sin(iTime) * 0.5 + 0.5;
    float repel = cos(iTime) * 0.5 + 0.5;

    // Calculate particle velocity
    vec3 vel = vec3(
        noise(pos.xz + iTime),
        noise(pos.yz + iTime + 123.456),
        noise(pos.xy + iTime + 678.9)
    ) * speed;

    // Add turbulence effect
    float turb = turbulent(pos * 0.5);
    vel *= 1.0 + turb * 0.5;

    // Calculate particle position over time
    pos += vel * iTime;

    // Create glowing particles with color transitions
    vec3 color = hsl2rgb(vec3(
        distanceToMouse * 360.0,
        0.7,
        0.5 + sin(iTime) * 0.5
    ));

    // Add glow effect based on particle density
    float glow = length(uv - pos.xy) * 10.0;
    color *= smoothstep(0.0, 1.0, glow);

    return vec4(color, 1.0);
}
