vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for swirling effect
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime; // Add time-based rotation

    // Create swirly pattern using sine waves and turbulence
    float swirl = sin(a * 4.0 + iTime * 2.0) * 0.5 + 0.5;
    float distortion = sin(r * 10.0 + iTime) * 0.3;

    // Add noise for organic texture
    vec2 noisePos = uv * 5.0 + iTime;
    float n = turbulent(noisePos);

    // Combine effects
    a += swirl * 0.2 + distortion;
    r *= 1.0 + n * 0.3;

    // Create rotating gradient using HSL colors
    vec3 color = hsl(a * 0.5, 0.7, 0.6);
    color = hsl2rgb(color);

    // Add noise-based modulation to color
    color *= 1.0 + n * 0.4;

    return vec4(color, 1.0);
}
