vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create a rotating beam pattern using noise and time
    float time = iTime * 0.5;
    float beamAngle = mod(theta + time, PI);

    // Add some turbulence to the beam shape
    vec3 noisePos = vec3(uv * 10.0, time);
    float turbulence = turbulent(noisePos) * 0.2;

    // Create color dispersion effect
    vec3 colorHSL = vec3(beamAngle / PI + 0.5, 1.0, r * 0.5 + 0.5 + turbulence);
    vec3 rgbColor = hsl2rgb(colorHSL);
    // Combine beam shape and colors
    float intensity = max(0.0, cos(r * 4.0 - time) * 0.5 + 0.5);
    rgbColor *= intensity;
    return vec4(rgbColor, 1.0);
}
