vec4 Effect2(vec2 uv) {
    // Create rotating pattern based on time and UV coordinates
    float t = iTime * 0.5;
    vec2 st = uv * 2.0 - 1.0;

    // Polar coordinates for rotation effect
    float r = length(st);
    float theta = atan2(st.y, st.x) + t;

    // Add noise and turbulence to create dynamic patterns
    float n = noise(uv * 5.0 + vec2(t));
    float turb = turbulent(uv * 10.0 + vec2(t*0.5));

    // Create color transitions using HSL
    vec3 color = hsl(theta * 2.0, r * 0.5 + 0.5, n * 0.5 + 0.5);

    // Add geometric patterns
    float pattern = step(0.7, sin(theta * 6.0 + t)) *
                    step(0.3, cos(theta * 4.0 + t*0.5));

    // Combine elements for final effect
    vec3 finalColor = color * (1.0 - turb * 0.5) + pattern * 0.7;

    return vec4(finalColor, 1.0);
}
