vec4 Effect2(vec2 uv) {
    // Create a base pattern using UV coordinates
    vec2 st = uv * 2.0 - 1.0;
    float dist = length(st);

    // Add some time-based movement
    float time = iTime * 0.5;
    float radius = sin(time + dist * 4.0) * 0.5 + 0.5;

    // Use noise to create a more organic pattern
    vec3 color = hsl2rgb(vec3(
        mod(dist * 10.0 + time, 1.0),
        1.0,
        0.5
    ));

    // Add some turbulence for dynamic effect
    float turb = turbulent(st * 4.0 + vec2(time));
    color *= 1.0 + turb * 0.3;

    return vec4(color, 1.0);
}
