vec4 Effect2(vec2 uv) {
    vec2 p = uv;

    // Convert to polar coordinates
    float r = length(p);
    float theta = atan2(p.y, p.x) / (2.0 * PI);  // Normalize between -0.5 and 0.5

    // Create a radial sawtooth pattern
    float radialFreq = 4.0;
    float sawtooth = mod(theta * radialFreq + iTime, 1.0);

    // Add turbulence for geometric complexity
    float noiseScale = 2.0;
    float turbulentValue = turbulent(vec2(p * noiseScale));

    // Combine patterns with time offset
    vec2 index = vec2(sawtooth - fract(turbulentValue + iTime * 0.5), r);

    // Sample noise texture and convert to alpha
    float noiseAlpha = noise(index) * 0.5;
    noiseAlpha = smoothstep(0.0, 1.0, noiseAlpha);

    // Set color based on noise value
    vec3 color = hsv2rgb(vec3(noiseAlpha, 0.7, 0.8));

    return vec4(color, noiseAlpha);
}
