vec4 Effect2(vec2 uv) {
    // Center and polar coordinates
    vec2 st = uv - 0.5;
    float r = length(st);
    float theta = atan2(st.y, st.x);

    // Create three blades at 120 degree angles
    float blade1 = mod(theta + 1.047, 2.094); // 120 degrees in radians
    float blade2 = mod(theta + 2.094, 2.094);
    float blade3 = mod(theta + 3.141, 2.094);

    // Blade shape using sawtooth wave
    float bladeShape = sin(blade1 * 6.0) + 
                      sin(blade2 * 6.0) + 
                      sin(blade3 * 6.0);

    // Add noise for dynamic effect
    float noiseValue = noise(vec2(r, iTime * 0.5));
    bladeShape += noiseValue * 0.3;

    // Main circle with glow
    float mainCircle = 1.0 - smoothstep(0.8, 1.0, r);
    mainCircle += turbulent(vec2(r, theta)) * 0.2;

    // Combine elements
    vec3 color = hsl2rgb(vec3(
        mod(theta * 5.0 + iTime, 1.0), // Hue variation
        0.7, // Saturation
        0.5 // Lightness
    ));

    float finalShape = mainCircle * (bladeShape * 0.6 + 0.4);
    return vec4(color * finalShape, 1.0);
}
