vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 center = uv - 0.5;
    float r = length(center);
    float theta = atan2(center.y, center.x);
    // Create hexagonal shape using polar coordinates
    theta += sin(iTime * 0.5 + theta) * 0.5; // Add some movement to the angles
    vec2 rotated = vec2(cos(theta), sin(theta));

    // Calculate distance from center in a rotating coordinate system
    float dist = length(vec2(
        r * cos(6.0 * (theta - iTime)), 
        r * sin(6.0 * (theta - iTime))
    )) / 15.0;

    // Create pulsating effect using sine wave with time modulation
    float pulse = sin(iTime + dist * 2.0) * 0.5 + 0.5;
    dist *= pulse;
    // Add noise to create dynamic variations
    vec3 color = hsl(
        iTime * 0.1 + fract(dist * 10.0), 
        0.7, 
        0.5 + turbulent(vec2(iTime, dist)) * 0.3
    );
    // Convert HSL to RGB and output
    return vec4(hsl2rgb(color), 1.0);
}
