vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5; // Add time offset for rotation

    // Create hexagonal pattern using sine waves
    float angle = fract(theta / (pi * 2.0 / 6.0)); // Divide circle into 6 segments
    float radius = sin(iTime * 3.0 + theta * 3.0) * 0.5 + 0.5; // Pulsating effect

    // Add noise for dynamic texture
    vec2 noisePos = vec2(r, angle);
    float noiseVal = noise(noisePos * 10.0); // High frequency noise

    // Combine radius and noise for final shape
    float d = r + (radius + noiseVal) * 0.5;

    // Create color transitions using HSL
    vec3 hslColor = vec3(fract(theta / pi + iTime), 1.0, 0.8);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Mix colors based on distance and noise
    float mixAmount = smoothstep(0.5, 1.0, d);
    vec3 finalColor = mix(vec3(0.0), rgbColor, mixAmount);

    return vec4(finalColor, 1.0);
}
