vec4 Effect2(vec2 uv) {
    // Create undulating pattern using sine waves
    float wave = sin(uv.x * 4.0 + iTime) + sin(uv.y * 4.0 + iTime);

    // Add time-based movement to the pattern
    vec2 st = uv * 3.0 + vec2(iTime, 0.0);

    // Generate color using HSL with rotating hue
    vec3 color = hsl2rgb(vec3(sin(iTime * 0.5) * 0.5 + 0.5, 0.7, 0.6));

    // Combine wave pattern with color
    vec4 finalColor = vec4(color * (wave * 0.5 + 0.5), 1.0);

    return finalColor;
}
