vec4 Effect2(vec2 uv) {
    // Create base noise pattern with turbulence
    float noise1 = turbulent(uv * 5.0 + iTime);
    vec3 color = hsl2rgb(vec3(0.6, 0.7, 0.5));

    // Add pulsating effect using time and sine wave
    float pulse = sin(iTime * 0.5) * 0.5 + 0.5;
    color *= pulse;

    // Create flowing lines pattern
    vec2 flowUV = uv * 10.0 - iTime;
    float line = sin(flowUV.x * 0.5 + iTime) * 0.5 + 0.5;
    line = turbulent(vec3(flowUV, 0.0)) * 0.5 + 0.5;

    // Combine patterns and add glow effect
    vec3 finalColor = mix(color, hsl2rgb(vec3(0.4, 0.8, 0.6)), line);
    finalColor *= noise1 * 0.5 + 0.5;

    return vec4(finalColor, 1.0);
}
