vec4 Effect2(vec2 uv) {
    // Create a pulsating nebula effect with dynamic colors and motion

    // Calculate distance from center
    vec2 pos = uv * 2.0 - 1.0;
    float dist = length(pos);

    // Add time-based pulsation
    float timePulse = sin(iTime * 0.5) * 0.5 + 0.5;

    // Create color layers using HSL
    vec3 baseColor = hsl2rgb(vec3(0.6, 1.0, 0.5));
    vec3 secondaryColor = hsl2rgb(vec3(0.3, 1.0, 0.7));

    // Add noise and turbulence for dynamic patterns
    float noiseVal = turbulent(pos * 4.0 + iTime);
    float distNoise = turbulent(pos * 2.0 + vec2(iTime)) * 0.5;

    // Create radial motion effect
    float angle = atan2(pos.y, pos.x) + timePulse * 2.0;
    float radius = length(pos) + sin(angle * 3.0 + iTime) * 0.1;

    // Mix colors based on distance and noise
    vec3 color = mix(baseColor, secondaryColor, smoothstep(0.5, 1.5, dist + noiseVal));

    // Add pulsation effect to color intensity
    float intensity = sin(radius * 8.0 + iTime) * 0.5 + 0.5;
    color *= (intensity + timePulse) * 0.7;

    // Create a glowing outer layer
    float glow = smoothstep(1.2, 1.4, dist);
    color += secondaryColor * glow * 0.8;

    return vec4(color, 1.0);
}
