vec4 Effect2(vec2 uv) {
    // Create a time-varying base pattern
    float t = iTime * 0.5;

    // Generate noise layers with different scales
    vec2 st = uv * 8.0;
    float n1 = noise(st + t);
    float n2 = noise(st * 2.0 + t * 0.7) * 0.5;
    float n3 = noise(st * 4.0 + t * 0.4) * 0.3;

    // Combine noise layers
    float pattern = (n1 + n2 + n3) * 0.5 + 0.5;

    // Add rotation effect using polar coordinates
    vec2 toCenter = uv - 0.5;
    float angle = atan(toCenter.y, toCenter.x);
    float dist = length(toCenter);
    angle += t * 4.0; // Rotate over time

    // Create color cycling effect using HSL colors
    float hue = sin(angle + t) * 0.5 + 0.5;
    vec3 hslColor = vec3(hue, 1.0, 0.7);

    // Convert to RGB and mix with noise pattern
    vec3 color = hsl2rgb(hslColor) * pattern;

    // Add some transparency for a glowing effect
    return vec4(color, pattern * 0.8);
}
