vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float radius = length(st);
    float angle = atan2(st.y, st.x);
    // Create rotating color wheels using HSL colors
    float hue = (angle + iTime) / (2.0 * PI);
    vec3 hslColor = vec3(hue, 1.0, 0.5);

    // Add pulsating rings effect
    float pulse = sin(iTime * 2.0 + radius * 8.0) * 0.5 + 0.5;
    float ringIntensity = abs(sin(radius * PI * 4.0 - iTime)) * pulse;
    // Combine with noise for more complex patterns
    vec3 noiseColor = vec3(noise(vec2(angle, radius)), 
                          noise(vec2(angle + PI/2.0, radius)),
                          noise(vec2(angle + PI, radius)));

    // Mix colors and effects
    vec3 finalColor = hsl2rgb(hslColor) * (1.0 - ringIntensity) + noiseColor * ringIntensity;

    return vec4(finalColor, 1.0);
}
