vec4 Effect2(vec2 uv) {
    // Calculate distance from center
    vec2 pos = uv - 0.5;
    float dist = length(pos);

    // Create radial waves with increasing frequency
    float t = iTime * 0.5;
    float radius = dist * 10.0 + sin(t * 2.0) * 3.0;
    float wave1 = sin(radius + t) * 0.5;
    float wave2 = sin(radius * 1.5 - t) * 0.4;

    // Add intertwining sine waves in different directions
    vec2 st = uv * 8.0;
    float xWave = sin(st.x * pi + t) * 0.3;
    float yWave = sin(st.y * pi + t + PI/2.0) * 0.3;
    float diagWave = sin((st.x + st.y) * pi + t + PI) * 0.2;

    // Combine waves with dynamic color shifts
    vec3 color1 = hsl2rgb(vec3(0.5 + wave1, 0.7, 0.8));
    vec3 color2 = hsl2rgb(vec3(0.3 + wave2, 0.6, 0.7));

    // Create pulsating center effect
    float pulse = sin(t * 4.0) * 0.5 + 0.5;
    float centerEffect = smoothstep(0.8, 1.0, dist * 2.0);
    vec3 finalColor = mix(color1, color2, wave1 + wave2 + xWave + yWave + diagWave);

    // Add turbulence and noise
    vec3 noisePos = vec3(uv * 4.0, t);
    float turb = turbulent(noisePos) * 0.2;
    finalColor += turb * (1.0 - centerEffect);

    return vec4(finalColor, 1.0);
}
