vec4 Effect2(vec2 uv) {
    // Create center point
    vec2 st = uv - 0.5;

    // Add rotation based on time
    float angle = sin(iTime * 0.5) + cos(iTime);
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 rotated = rot * st;

    // Create distance field with noise and turbulence
    float d = length(rotated);
    d += noise(vec3(d, iTime * 0.1, 0.0)) * 0.5;
    d += turbulent(vec2(d, iTime)) * 0.3;

    // Map distance to color using HSV
    vec3 hsvColor = vec3(
        (d + iTime) * 0.2,
        0.7 + noise(vec3(d, iTime, 1.0)) * 0.3,
        smoothstep(0.0, 1.0, 1.0 - d)
    );

    // Convert to RGB and add ghost effect
    vec3 color = hsv2rgb(hsvColor);
    color *= smoothstep(0.8, 1.0, d);

    // Add inner glow
    float glow = smoothstep(0.6, 0.7, d) * 0.5;
    color += glow;

    return vec4(color, 1.0);
}
