vec4 Effect2(vec2 uv) {
    // Create a pulsating plasma effect with geometric overlays

    // Base noise pattern with time transformation
    float time = iTime * 0.5;
    vec3 noise1 = vec3(noise(uv * 10.0 + time), 
                       noise(uv * 10.0 + time * 0.7),
                       noise(uv * 10.0 + time * 0.4));

    // Add turbulence for more dynamic patterns
    float turb = turbulent(uv * 5.0 + vec2(time, time)) * 0.5 + 0.5;

    // Create geometric overlay pattern
    vec3 geomPattern = vec3(1.0);
    geomPattern.r *= sin(uv.x * 4.0 + time) * 0.5 + 0.5;
    geomPattern.g *= cos(uv.y * 4.0 + time * 0.7) * 0.5 + 0.5;
    geomPattern.b *= sin((uv.x + uv.y) * 6.0 + time * 0.3) * 0.5 + 0.5;

    // Combine noise and geometric patterns
    vec3 finalColor = mix(noise1, geomPattern, turb);

    // Add color shifting based on viewer movement (simulated by time)
    vec3 hslColor = vec3(finalColor.r * 0.7 + 0.3,
                         finalColor.g * 0.6 + 0.4,
                         finalColor.b * 0.5 + 0.5);

    // Convert to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
