vec4 Effect2(vec2 uv) {
    // Scale UV coordinates and add time variation
    vec2 st = uv * 5.0 + iTime * 0.5;

    // Create a sine wave pattern with time offset
    float t = iTime * 0.5;
    float x = sin(st.x * 2.0 + t) * 0.5 + 0.5;
    float y = sin(st.y * 3.0 + t) * 0.5 + 0.5;

    // Combine patterns and add turbulence
    float pattern = abs(x - y) + turbulent(vec2(uv * 10.0));

    // Convert to HSL color space with time-based hue shift
    vec3 color = hsl(pattern * 0.6 + 0.3, 0.8, 0.5);
    color = hsl2rgb(color);

    return vec4(color, 1.0);
}
