vec4 Effect2(vec2 uv) {
    // Time-based animation speed
    float time = iTime * 0.5;

    // Base color in HSL space (fire-like colors)
    vec3 hslColor = vec3(0.5, 1.0, 0.8);

    // Create flowing patterns using noise and turbulence
    vec2 st = uv * 4.0 + time;
    float flow = turbulent(st) * 0.5 + 0.5;

    // Add pulsing effect
    float pulse = sin(time * 3.0) * 0.5 + 0.5;

    // Combine patterns and colors
    vec3 color = hsl2rgb(hslColor);
    color *= flow * (1.0 + sin(time * 2.0)) * 0.5;
    color += vec3(pulse, pulse, pulse) * 0.3;

    // Add depth with multiple layers of noise
    float layer1 = turbulent(vec3(uv * 2.0, time)) * 0.4;
    float layer2 = turbulent(vec3(uv * 4.0 + time, 0.0)) * 0.6;
    float layer3 = turbulent(vec3(uv * 8.0 - time, 1.0)) * 0.8;

    // Combine layers and apply gamma correction
    vec3 finalColor = (layer1 + layer2 + layer3) * color;
    finalColor = pow(finalColor, vec3(0.7));

    return vec4(finalColor, 1.0);
}
