vec4 Effect2(vec2 uv) {
    // Scale UV coordinates based on time for movement
    vec2 p = uv * (1.0 + sin(iTime) * 0.5);

    // Apply noise and turbulence to create dynamic patterns
    float n = noise(vec2(p.x, p.y)) + turbulent(vec2(p.x*0.5, p.y*0.5));

    // Create pulsating grid lines
    float gridX = fract(p.x * 10.0);
    float gridY = fract(p.y * 10.0);
    float grid = (min(gridX, 1.0 - gridX) + min(gridY, 1.0 - gridY)) * sin(iTime);

    // Combine noise and grid patterns
    float value = n + grid;

    // Convert to RGB using HSL color space for dynamic colors
    vec3 color = hsl2rgb(vec3(value*0.6 + 0.3, 0.8, 0.5));

    return vec4(color, 1.0);
}
