vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar (distance from center)
    float d = length(uv);

    // Create concentric rings using time-based expansion
    float t = iTime * 0.5;
    float ring = floor(d + t * 0.2) * 0.2;

    // Create color cycling effect using HSL colors
    vec3 color1 = hsl(ring * 360.0, 0.7, 0.5);
    vec3 color2 = hsl((ring + 1.0) * 360.0, 0.7, 0.5);

    // Mix colors with time-based fade
    float mixAmount = sin(t * 0.5 + ring * pi) * 0.5 + 0.5;
    vec3 finalColor = mix(color1, color2, mixAmount);

    // Add noise for ripples effect
    vec2 noiseUV = uv * 8.0 + t;
    float n = noise(noiseUV) * 0.1;
    d += n;

    // Create fading rings using distance from center
    float intensity = sin((d - t) * 4.0) * 0.5 + 0.5;
    finalColor *= intensity;

    return vec4(finalColor, 1.0);
}
