vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create base geometric pattern using sine waves and noise
    vec2 st = uv * 16.0 + time;
    float pattern = sin(st.x * 0.5 + time) * cos(st.y * 0.5) +
                    sin(st.x * 0.7 + time * 0.3) * cos(st.y * 0.9);

    // Add noise and turbulence
    vec2 noiseSt = uv * 8.0;
    float noiseVal = noise(noiseSt + time) * 0.5;
    pattern += noiseVal;

    // Create flowing gradient effect
    vec3 color1 = hsl2rgb(vec3(0.2, 0.7, 0.5));
    vec3 color2 = hsl2rgb(vec3(0.8, 0.6, 0.4));
    float mixAmount = sin(uv.x * pi + time) * 0.5 + 0.5;
    vec3 baseColor = mix(color1, color2, mixAmount);

    // Add dynamic color shifts
    float hueShift = sin(time * 0.3) * 0.2;
    vec3 finalColor = hsv2rgb(vec3(hueShift + uv.x * 0.4, 0.7, pattern * 0.5 + 0.5));

    // Combine all elements with pow for better contrast
    float brightness = pow(abs(pattern), 1.5) * 0.6 + 0.4;
    finalColor *= brightness;

    return vec4(finalColor, 1.0);
}
