vec4 Effect2(vec2 uv) {
    vec3 baseColor = hsl(0.5 + 0.1 * sin(iTime * 0.1), 0.8, 0.5);
    float noiseScale = noise(uv * 500.0) * 0.5 + 0.5;
    float pulse = turbulent(vec3(uv * 20.0, iTime)) * 0.5 + 0.5;

    vec3 coralColor = hsl2rgb(vec3(
        0.6 + 0.1 * sin(iTime * 0.1),
        0.8,
        0.4 + noiseScale * 0.2
    ));

    float lightPulse = sin(iTime) * 0.5 + 0.5;
    vec3 lighting = vec3(
        1.0 - length(uv - 0.5),
        1.0 - length(uv - 0.5),
        1.0 - length(uv - 0.5)
    ) * lightPulse;

    return vec4(coralColor * (pulse + noiseScale) * 0.8 + lighting, 1.0);
}
