vec4 Effect2(vec2 uv) {
    vec3 color = hsl2rgb(vec3(0.5 + 0.5 * sin(iTime * 0.5), 1.0, 0.5));

    vec2 st = uv * 8.0;
    vec2 pos = fract(st - iTime * 0.5) * 2.0 - 1.0;

    float d1 = length(abs(pos.x) + abs(pos.y)) * 0.7;
    float d2 = length(abs(pos.x + 0.5) + abs(pos.y + 0.5)) * 0.7;
    float d3 = length(abs(pos.x - 0.5) + abs(pos.y - 0.5)) * 0.7;

    float mask = smoothstep(1.0, 0.0, abs(d1 - 0.2)) +
                 smoothstep(1.0, 0.0, abs(d2 - 0.3)) +
                 smoothstep(1.0, 0.0, abs(d3 - 0.4));

    vec2 noisePos = uv * 5.0 + iTime;
    float n = noise(noisePos) * 0.5 + 0.5;

    color *= (mask + n * 0.3);
    color += hsl2rgb(vec3(0.3, 1.0, 0.8)) * turbulent(uv * 4.0 + iTime) * 0.2;

    return vec4(color, 1.0);
}
