vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 polar = vec2(length(uv), atan2(uv.y, uv.x));

    // Create a hexagonal grid pattern
    float angle = polar.y * 6.0; // Multiply by 6 for hexagon symmetry
    vec2 q = vec2(cos(angle), sin(angle)) * polar.x;
    vec2 grid = floor(q + 0.5);

    // Add pulsating color effect
    float t = sin(iTime * 0.5 + uv.x * 2.0 + uv.y * 3.0) * 0.5 + 0.5;

    // Create HSL color based on grid position and time
    vec3 hslColor = vec3(
        (grid.x + grid.y) * 0.1 + t,
        1.0,
        0.5
    );

    // Convert to RGB and add noise for variation
    vec3 rgbColor = hsl2rgb(hslColor);
    rgbColor += noise(vec3(uv * 10.0, iTime)) * 0.2;

    return vec4(rgbColor, 1.0);
}
