vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime; // Add time for rotation

    // Create layers of rotating shapes
    float layer1 = turbulent(vec3(r*5.0, a*4.0, iTime*0.5));
    float layer2 = turbulent(vec3(r*3.0, a*6.0, iTime*0.7));

    // Combine layers with different frequencies
    float pattern = mix(layer1, layer2, 0.5);

    // Add color transformation based on angle and time
    vec3 baseColor = hsl(a * 0.5 + iTime, 0.8, 0.6);
    vec3 dynamicColor = hsv2rgb(vec3((a * 0.7 + iTime) * 2.0, 1.0, 1.0));

    // Combine colors and pattern
    vec3 finalColor = mix(baseColor, dynamicColor, abs(pattern * 0.5 + 0.5));

    // Add brightness variation
    finalColor *= pow(r, 2.0) * 0.8 + 0.2;

    return vec4(finalColor, 1.0);
}
