vec4 Effect2(vec2 uv) {
    // Rotate and scale UV coordinates
    float t = iTime * 0.5;
    vec2 st = uv * 10.0;

    // Create rotating sine wave patterns
    float angle = t + sin(t * 0.7);
    vec2 rotatedUV = vec2(
        cos(angle) * st.x - sin(angle) * st.y,
        sin(angle) * st.x + cos(angle) * st.y
    );

    // Layered sine waves with different frequencies
    float wave1 = sin(rotatedUV.x * 0.5 + t) * 0.5 + 0.5;
    float wave2 = sin(rotatedUV.y * 0.7 - t * 0.3) * 0.4 + 0.4;

    // Combine waves and add turbulence
    float combined = (wave1 + wave2) * 0.8;
    combined += turbulent(vec2(st.x * 0.5, st.y * 0.5)) * 0.2;

    // Dynamic color palette using HSL
    vec3 color = hsl(
        mod(t * 0.1 + combined * 2.0, 1.0),
        0.7,
        0.6
    );

    // Add noise to colors for extra detail
    color += vec3(noise(vec3(st.x * 0.5, st.y * 0.5, t)) * 0.2);

    return vec4(color, 1.0);
}
