vec4 Effect2(vec2 uv) {
    // Translate UV to center at (0,0)
    vec2 st = uv - 0.5;

    // Convert to polar coordinates
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime; // Add time for rotation

    // Create color wheel effect with pulsing
    vec3 colorHSL = vec3(
        (theta / (2.0 * PI)) * 0.5 + 0.5 + sin(iTime)*0.2, // Hue with pulsing
        0.7 + turbulent(vec3(r, iTime*0.1, 0.0))*0.3, // Saturation with turbulence
        0.5 + r * 0.4 - sin(iTime*0.5)*0.2 // Lightness with radial gradient and pulse
    );

    // Convert to RGB and add turbulence effect
    vec3 color = hsl2rgb(colorHSL) + turbulent(vec3(r, theta, iTime)) * 0.2;

    return vec4(color, 1.0);
}
