vec4 Effect2(vec2 uv) {
    // Time-based modulation
    float t = iTime * 0.5;

    // Scale UV coordinates for wave pattern
    vec2 st = uv * 8.0 + t; // Increase frequency with scale

    // Create interweaving sine waves in x and y directions
    float waveX = sin(st.x + t) * 0.5 + 0.5;
    float waveY = sin(st.y + t + PI/2.0) * 0.5 + 0.5; // Offset phase for interweaving

    // Combine waves and add turbulence
    vec2 noiseUV = uv * 4.0 + vec2(t, 0.0); // Scale for noise pattern
    float turbulence = turbulent(noiseUV);

    // Mix wave patterns with turbulence
    float pattern = (waveX + waveY) * 0.5 + turbulence * 0.3;

    // Create pulsing effect by modulating the pattern
    float pulse = sin(t * 2.0) * 0.5 + 0.5; // Pulsation between 0 and 1
    pattern = mix(pattern, pattern * 2.0, pulse); // Increase contrast with pulse

    // Convert to color using HSL to RGB conversion
    vec3 hslColor = vec3(t * 0.2, 1.0, pattern); // Hue varies with time
    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
