vec4 Effect2(vec2 uv) {
    // Create moving sine wave patterns
    float wave1 = sin(uv.x * 8.0 + iTime * 2.0);
    float wave2 = sin(uv.y * 6.0 + iTime * 3.0);

    // Combine waves with noise texture
    vec2 noiseUV = uv * 10.0;
    float noiseValue = noise(noiseUV) * 0.5 + 0.5;

    // Mix waves and noise
    float pattern = (wave1 + wave2) * noiseValue;

    // Add turbulence for more dynamic movement
    vec3 turbPos = vec3(uv.x, uv.y, iTime);
    float turb = turbulent(turbPos * 0.5);

    // Combine everything with color transformation
    vec3 color = hsl2rgb(vec3(
        mod(iTime * 0.1 + pattern * 0.5, 1.0),
        0.7,
        0.6
    )) * (1.0 + turb * 0.3);

    return vec4(color, 1.0);
}
