vec4 Effect2(vec2 uv) {
    // Create grid pattern using floor and fract functions
    vec2 st = floor(uv * 50.0);

    // Calculate distance from center of each square
    vec2 offset = uv - (floor(uv * 50.0) + 0.5)/50.0;
    float dist = length(offset * 50.0);

    // Create pulsating animation using sine wave
    float pulse = sin(iTime + dist * 2.0) * 2.0;

    // Calculate color transition from red to blue
    vec3 hslColor = vec3(0.5 + pulse*0.5, 1.0, 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add turbulence for extra visual interest
    float turb = turbulent(uv * 10.0) * 0.1;
    rgbColor += turb;

    return vec4(rgbColor, 1.0);
}
