vec4 Effect2(vec2 uv) {
    // Create base pattern with sine waves
    vec3 pattern = vec3(
        sin(uv.x * 4.0 + iTime) * 0.5 + 0.5,
        sin(uv.y * 4.0 + iTime * 0.7) * 0.5 + 0.5,
        sin(uv.x * 2.0 + uv.y * 2.0 + iTime * 0.3) * 0.5 + 0.5
    );

    // Add noise and turbulence for more complex patterns
    vec2 st = uv * 10.0;
    float n = noise(st);
    pattern += vec3(n * 0.3, n * 0.4, n * 0.5);

    // Create time-based color shifts using HSL
    vec3 hslColor = vec3(
        sin(iTime * 0.2) * 0.5 + 0.5,
        0.7,
        0.8
    );
    vec3 rgbColor = hsl2rgb(hslColor);

    // Combine patterns and colors with turbulence
    float t = turbulent(vec2(uv.x * 4.0, uv.y * 4.0 + iTime));
    pattern *= mix(rgbColor, vec3(1.0), t * 0.5 + 0.5);

    // Add brightness variation
    pattern += sin(iTime) * 0.2;

    return vec4(pattern, 1.0);
}
