vec4 Effect2(vec2 uv) {
    // Create moving stripes using sine waves
    float speed = 0.5;
    float frequency = 10.0;

    // Calculate base stripe pattern with time offset
    float stripe = sin(uv.x * frequency + iTime * speed);

    // Add pulsating effect by varying the color mix over time
    float pulse = sin(iTime * 0.5) * 0.5 + 0.5;

    // Create color transition from red to blue using HSL
    vec3 color1 = hsl(0.0, 1.0, 0.5);
    vec3 color2 = hsl(240.0, 1.0, 0.5);
    vec3 mixedColor = mix(color1, color2, abs(stripe) * 0.5 + pulse * 0.5);

    // Add noise for extra texture
    vec2 noiseUV = uv * 100.0;
    float noiseValue = noise(noiseUV) * 0.3;
    mixedColor += noiseValue;

    // Create pulsating effect by varying stripe intensity
    float intensity = sin(iTime + uv.y * pi) * 0.5 + 0.5;
    vec3 finalColor = mixedColor * (intensity + 0.5);

    return vec4(finalColor, 1.0);
}
