vec4 Effect2(vec2 uv) {
    // Create dynamic color shifts using time
    float time = iTime * 0.5;

    // Convert to polar coordinates for radial patterns
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x);
    float r = length(st) * 0.75;

    // Create layered noise patterns with turbulence
    float n1 = turbulent(vec3(r + time, theta * 4.0, 0.0));
    float n2 = turbulent(vec3(r * 2.0 - time, theta * 2.0, 0.0)) * 0.5;
    float n3 = noise(vec3(r * 4.0 + time, theta * 8.0, 0.0)) * 0.25;

    // Combine noise layers
    float pattern = (n1 + n2 + n3) * 0.5 + 0.5;

    // Create color shifts using HSL
    vec3 color = hsl(
        theta * 0.5 + time,
        0.7 + sin(r * 4.0 + time) * 0.3,
        pattern * 0.6 + 0.2
    );

    // Add depth and movement to the colors
    color *= sin(pattern * pi + time) * 0.5 + 0.5;

    return vec4(color, 1.0);
}
