vec4 Effect2(vec2 uv) {
    // Base color with time-based pulsing
    vec3 baseColor = hsl(0.6 + 0.1*sin(iTime*0.5), 0.8, 0.1);

    // Create dynamic noise pattern with turbulence
    float t = iTime * 0.2;
    vec2 st = uv * 4.0; // Scale coordinates for more detail

    // Mix different noise layers
    float n1 = turbulent(st + t) * 0.5;
    float n2 = turbulent(st*2.0 + t*3.0) * 0.3;
    float n3 = noise(vec3(st, t)) * 0.2;

    // Combine noise layers with color
    vec3 color = baseColor * (n1 + n2 + n3);

    // Add pulsating effect
    float pulse = sin(iTime) * 0.5 + 0.5;
    color *= pulse;

    // Create movement using sine waves
    vec2 movement = vec2(sin(iTime*0.3)*0.1, cos(iTime*0.4)*0.1);
    color += hsl(0.2 + movement.x, 0.7, 0.5) * 0.3;

    // Add glow effect
    float glow = length(uv - 0.5) * 2.0;
    glow = 1.0 - smoothstep(0.8, 1.2, glow);
    color += vec3(0.7, 0.4, 0.9) * glow * 0.5;

    return vec4(color, 1.0);
}
