vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Generate swarm points using noise and time
    for (int i = 0; i < 20; i++) {
        float t = iTime * 0.5 + float(i);
        vec2 pos = sin(vec2(t, t * 1.3)) * 0.4 + uv;

        // Add noise to position
        pos += noise(vec3(pos, iTime)) * 0.1;

        // Calculate distance from current point
        vec2 d = uv - pos;
        float dist = length(d);

        // Create color based on distance and time
        vec3 c = hsl(dist * 5.0 + iTime, 0.8, 0.6);

        // Add glow effect
        if (dist < 0.1) {
            color += c * (1.0 - dist * 10.0);
        }
    }

    // Add background turbulence
    vec2 bgPos = uv * 5.0 + iTime;
    float bgNoise = turbulent(bgPos);
    color += hsl(1.0, 0.3, 0.1) * bgNoise * 0.3;

    return vec4(color, 1.0);
}
