vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 8.0 + iTime;

    // Add noise and turbulence for dynamic movement
    vec2 noiseOffset = vec2(noise(st.xy + iTime), noise(st.xy - iTime)) * 0.5;
    st += noiseOffset;

    // Create rotating geometric patterns
    float angle = atan2(uv.y - 0.5, uv.x - 0.5);
    vec2 line1 = vec2(sin(angle + iTime) * 0.5, cos(angle + iTime) * 0.5);
    vec2 line2 = vec2(sin(angle - iTime) * 0.5, cos(angle - iTime) * 0.5);

    // Mix patterns with noise
    float pattern = turbulent(st.xy) * 0.5 + 0.5;
    pattern += length(line1 + line2) * 0.5;

    // Create color transitions using HSL
    vec3 color = hsl(angle * 0.5 + iTime * 0.1, 0.7, 0.6);
    color = hsl2rgb(color);

    // Add glowing effect
    float glow = exp(-length(uv - 0.5));
    color *= (pattern + glow) * 1.5;

    return vec4(color, 1.0);
}
