vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to range [-1, 1]
    vec2 pos = uv * 2.0 - 1.0;

    // Calculate distance from center and angle
    float radius = length(pos);
    float angle = atan2(pos.y, pos.x);

    // Add time-based pulsation
    radius += sin(iTime * 0.5 + angle) * 0.3;

    // Create color variations using HSL
    vec3 baseColor = hsl(0.7, 1.0, 0.5); // Purple base color

    // Add turbulence and noise for dynamic effect
    float turb = turbulent(vec2(radius * 4.0 + iTime, angle));

    // Create gradient effect based on radius
    vec3 color = mix(
        baseColor,
        hsl(0.1, 1.0, 0.5), // Orange secondary color
        smoothstep(0.0, 1.0, radius * 0.8)
    );

    // Add pulsating glow effect
    float glow = sin(iTime + radius * 2.0) * 0.3;
    color += vec3(glow);

    // Convert to RGB and return
    return vec4(hsl2rgb(color), 1.0);
}
