vec4 Effect2(vec2 uv) {
    // Create time-based variation
    float t = iTime * 0.5;

    // Add sine wave movement to UV coordinates
    vec2 st = uv * 10.0 + vec2(sin(t * 3.0), cos(t * 2.0));

    // Use turbulence for flowing pattern
    float noise1 = turbulent(st);
    float noise2 = turbulent(st * 0.5 + t);

    // Create color gradient using HSL conversion
    vec3 baseColor = hsl2rgb(vec3(0.5 + noise1 * 0.5, 0.7, 0.8));
    vec3 mixColor = hsl2rgb(vec3(0.3 + noise2 * 0.5, 0.6, 0.7));

    // Combine colors with noise
    vec3 finalColor = mix(baseColor, mixColor, abs(noise(st * 0.1)));

    // Add glow effect using low-frequency turbulence
    float glow = turbulent(vec2(uv.x * 5.0 + t, uv.y * 5.0 - t)) * 0.5 + 0.5;
    finalColor *= glow * 2.0;

    return vec4(finalColor, 1.0);
}
