vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create multiple overlapping sine wave layers with varying frequencies
    vec3 waves = vec3(
        sin(uv.x * 10.0 + time) * 0.5 + 0.5,
        sin(uv.y * 20.0 - time * 2.0) * 0.5 + 0.5,
        sin((uv.x + uv.y) * 30.0 + time * 3.0) * 0.5 + 0.5
    );

    // Add noise and turbulence to create more dynamic patterns
    vec2 noiseScale = vec2(100.0, 100.0);
    float noiseValue = noise(uv * noiseScale + time);

    // Create cyclic color shifts using HSL conversion
    vec3 color = hsl(
        mod(time * 0.5, 1.0),   // Hue cycling over time
        0.7,                    // Saturation
        waves.x * 0.5 + 0.5     // Value modulation by wave pattern
    );

    // Combine everything with noise and turbulence
    vec3 finalColor = hsl2rgb(color) * (noiseValue * 0.5 + 0.5);

    return vec4(finalColor, 1.0);
}
