vec4 Effect2(vec2 uv) {
    // Center and scale UV coordinates
    vec2 st = (uv - 0.5) * 16.0;

    // Convert to polar coordinates
    float theta = atan2(st.y, st.x);
    float r = length(st);

    // Add time-based rotation
    theta += iTime * 0.5;

    // Create rotating sine wave pattern
    float wave = sin(theta) * cos(r + iTime) * 4.0 +
                 sin(2.0*theta + iTime) * r * 0.5 +
                 cos(3.0*theta - iTime) * r * 0.3;

    // Add turbulence for more dynamic effect
    wave += turbulent(vec2(theta, r)) * 0.4;

    // Convert to color using HSL
    vec3 color = hsl2rgb(vec3(
        mod(theta + iTime * 0.5, 2.0 * PI) / (2.0 * PI),
        0.7,
        0.6
    ));

    return vec4(color, 1.0);
}
