vec4 Effect2(vec2 uv) {
    // Create flowing noise pattern with time component
    vec3 p = vec3(uv * 5.0 + iTime, iTime * 0.5);
    float n1 = turbulent(p);
    float n2 = turbulent(p * 0.5 + 100.0);

    // Create color transitions using HSL
    vec3 color1 = hsl(0.6 + sin(iTime*0.5)*0.2, 0.8, 0.4);
    vec3 color2 = hsl(0.9 + cos(iTime*0.5)*0.2, 0.8, 0.4);

    // Combine noise layers with different frequencies
    float totalNoise = n1 * 0.5 + n2 * 0.3;

    // Create flowing pattern by mixing colors based on noise
    vec3 finalColor = mix(color1, color2, totalNoise);

    // Add metallic sheen effect using sine wave modulation
    float highlight = sin(uv.x * 10.0 + iTime) * sin(uv.y * 10.0 + iTime*0.5);
    finalColor += vec3(0.8) * highlight;

    return vec4(finalColor, 1.0);
}
