vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 0.5 + 0.5;
    float angle = atan2(uv.y, uv.x);
    float radius = length(uv) * 0.75;

    // Add time-based rotation
    angle += iTime * 0.1;

    // Create a base pattern using noise and turbulence
    vec2 noiseSt = st + vec2(sin(iTime)*0.3, cos(iTime)*0.3);
    float baseNoise = noise(noiseSt * 5.0);
    float turbulentVal = turbulent(st * 4.0 + iTime);

    // Create color variations using HSL
    vec3 color1 = hsl2rgb(vec3(angle*0.1, 0.7, 0.8));
    vec3 color2 = hsl2rgb(vec3((angle + iTime)*0.15, 0.6, 0.7));

    // Combine patterns with turbulence
    float pattern = mix(baseNoise, turbulentVal, 0.5);
    pattern += sin(radius * 8.0 + iTime) * 0.2;

    // Create layered effect
    vec3 finalColor = mix(color1, color2, pattern);
    finalColor *= 1.0 - abs(pattern - 0.5) * 2.0;

    return vec4(finalColor, 1.0);
}
