vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Generate random positions for fireflies using noise
    vec3 pos1 = vec3(noise(vec2(time, 0.0)) * 0.8 + 0.1,
                     noise(vec2(0.0, time)) * 0.8 + 0.1,
                     0.0);

    // Create a swarm of fireflies with varying colors and glow
    vec3 color = hsl(0.5, 0.7, 0.4); // Base green color for fireflies

    // Add dynamic glow effect using turbulence
    float glow = turbulent(vec2(uv * 10.0 + time)) * 0.5 + 0.5;

    // Create ambient light with noise texture
    vec3 ambient = hsl(0.6, 0.4, 0.2) * 0.2; // Dark blue ambient

    // Add multiple fireflies with different parameters
    for (int i = 0; i < 20; i++) {
        float speed = noise(vec2(float(i), time)) * 0.5 + 0.5;
        vec3 pos = vec3(uv.x + sin(time + float(i)) * 0.1,
                        uv.y + cos(time + float(i)) * 0.1,
                        0.0);

        // Calculate distance from firefly
        float dist = length(pos.xy - uv);

        // Create glowing effect with color transitions
        vec3 glowColor = mix(color, hsl(0.6, 0.8, 0.5), dist * 0.2);
        glowColor *= smoothstep(1.0, 0.0, dist * 4.0);

        // Add dynamic movement and interaction with ambient light
        float alpha = turbulent(vec2(pos.x * 10.0 + time, pos.y * 10.0 + time)) * 0.5 + 0.5;
        glowColor *= alpha;

        // Combine firefly glow with ambient background
        ambient += glowColor * 0.3;
    }

    return vec4(ambient, glow);
}
